DECLARE @Username NVARCHAR(128)\nSET @Username = 'your_username' -- Replace with the username you are checking\n\nCREATE TABLE #PermissionsReport (\n    TableName NVARCHAR(128),\n    SELECT_PERM CHAR(3),\n    DELETE_PERM CHAR(3),\n    UPDATE_PERM CHAR(3),\n    INSERT_PERM CHAR(3),\n    ALTER_PERM CHAR(3)\n)\n\nDECLARE @TableName NVARCHAR(128)\nDECLARE @SQL NVARCHAR(4000)\n\nDECLARE TableCursor CURSOR FOR\nSELECT TABLE_NAME\nFROM INFORMATION_SCHEMA.TABLES\nWHERE TABLE_TYPE = 'BASE TABLE'\n\nOPEN TableCursor\nFETCH NEXT FROM TableCursor INTO @TableName\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SET @SQL = 'INSERT INTO #PermissionsReport\n                SELECT ''' + @TableName + ''',\n                CASE WHEN HAS_PERMS_BY_NAME(''' + @TableName + ''', ''OBJECT'', ''SELECT'') = 1 THEN ''Yes'' ELSE ''No'' END,\n                CASE WHEN HAS_PERMS_BY_NAME(''' + @TableName + ''', ''OBJECT'', ''DELETE'') = 1 THEN ''Yes'' ELSE ''No'' END,\n                CASE WHEN HAS_PERMS_BY_NAME(''' + @TableName + ''', ''OBJECT'', ''UPDATE'') = 1 THEN ''Yes'' ELSE ''No'' END,\n                CASE WHEN HAS_PERMS_BY_NAME(''' + @TableName + ''', ''OBJECT'', ''INSERT'') = 1 THEN ''Yes'' ELSE ''No'' END,\n                CASE WHEN HAS_PERMS_BY_NAME(''' + @TableName + ''', ''OBJECT'', ''ALTER'') = 1 THEN ''Yes'' ELSE ''No'' END\n                FROM sysusers u\n                WHERE u.name = ''' + @Username + ''''\n\n    EXEC sp_executesql @SQL\n\n    FETCH NEXT FROM TableCursor INTO @TableName\nEND\n\nCLOSE TableCursor\nDEALLOCATE TableCursor\n\nSELECT * FROM #PermissionsReport\nORDER BY TableName\n\nDROP TABLE #PermissionsReport