WITH RECURSIVE SlotNumbers AS (\n    SELECT 1 AS Slot\n    UNION ALL\n    SELECT Slot + 1\n    FROM SlotNumbers\n    WHERE Slot < (SELECT MAX(Slots) FROM YourTable)\n),\nProjectedSlots AS (\n    SELECT \n        Name,\n        SlotValue,\n        Slots,\n        SlotNumbers.Slot\n    FROM \n        YourTable, \n        SlotNumbers\n    WHERE \n        SlotNumbers.Slot <= YourTable.Slots\n),\nSlotSums AS (\n    SELECT \n        Slot,\n        Name,\n        SUM(SlotValue) OVER (PARTITION BY Slot) AS SlotSum\n    FROM \n        ProjectedSlots\n)\nSELECT DISTINCT\n    Name,\n    SlotSum,\n    Slot\nFROM \n    SlotSums\nORDER BY \n    Slot;