-- Capture the state before deletion\nCREATE TEMP TABLE temp_table1 AS SELECT * FROM table1;\nCREATE TEMP TABLE temp_table2 AS SELECT * FROM table2;\n-- ...\n\n-- Perform your DELETE operations in a transaction\nBEGIN;\nDELETE FROM table1 WHERE condition;\nDELETE FROM table2 WHERE condition;\n-- ...\n\n-- Capture the state after deletion\nCREATE TEMP TABLE temp_table1_after AS SELECT * FROM table1;\nCREATE TEMP TABLE temp_table2_after AS SELECT * FROM table2;\n-- ...\n\n-- Compare before and after states\nSELECT * FROM temp_table1\nEXCEPT\nSELECT * FROM temp_table1_after;\n\nSELECT * FROM temp_table2\nEXCEPT\nSELECT * FROM temp_table2_after;\n\n-- If everything is as expected, commit the transaction, otherwise, rollback\nCOMMIT; -- or ROLLBACK;