-- Enable xp_cmdshell if it is not already enabled\nEXEC sp_configure 'show advanced options', 1;\nRECONFIGURE;\nEXEC sp_configure 'xp_cmdshell', 1;\nRECONFIGURE;\n\n-- Use xp_cmdshell to list the contents of a directory\nDECLARE @directory VARCHAR(255);\nSET @directory = 'C:\YourDirectoryPath';\n\nDECLARE @cmd VARCHAR(255);\nSET @cmd = 'dir ' + @directory;\n\n-- Execute the command\nEXEC xp_cmdshell @cmd;