BEGIN;\n\n-- Your DELETE statements here\nDELETE FROM table1 WHERE condition;\nDELETE FROM table2 WHERE condition;\n-- ...\n\n-- To check the effects before committing, use SELECT statements to verify the state of your data\nSELECT * FROM table1 WHERE condition;\nSELECT * FROM table2 WHERE condition;\n-- ...\n\n-- If everything looks good, commit the transaction, otherwise, rollback\nCOMMIT; -- or ROLLBACK;