WITH RECURSIVE CategoryHierarchy AS (\n    -- Anchor member: select the starting category\n    SELECT id, parent_id, name\n    FROM Categories\n    WHERE name = 'Processors'\n    \n    UNION ALL\n    \n    -- Recursive member: select the subcategories\n    SELECT c.id, c.parent_id, c.name\n    FROM Categories c\n    INNER JOIN CategoryHierarchy ch ON ch.id = c.parent_id\n)\nSELECT p.*\nFROM Products p\nINNER JOIN CategoryHierarchy ch ON p.category_id = ch.id;