CREATE TABLE Questions \n(\n    USER_ID integer REFERENCES User_info(USER_ID) \n                    PRIMARY KEY \n                    CHECK (USER_ID > 0), \n    QUESTION_ID integer REFERENCES Tags(QUESTION_ID) \n                        NOT NULL \n                        CHECK (USER_ID > 0), \n    QUESTION_BODY text NOT NULL,           -- question must have body \n    TITLE varchar(60) NOT NULL,            -- no empty title\n    MODERATOR_REMOVAL boolean NOT NULL DEFAULT false,    -- by default false\n    SENT_TIME timestamp NOT NULL\n);