CREATE TABLE Locales (\n    ID INT PRIMARY KEY,\n    LocationName VARCHAR(255) NOT NULL,\n    ParentID INT,\n    CONSTRAINT fk_parent FOREIGN KEY (ParentID) REFERENCES Locales(ID),\n    CONSTRAINT chk_self_parent CHECK (ID <> ParentID) -- Prevent self-referencing\n);\n\n-- Example data insertion\nINSERT INTO Locales (ID, LocationName, ParentID) VALUES (1, 'USA', NULL);\nINSERT INTO Locales (ID, LocationName, ParentID) VALUES (2, 'California', 1);\nINSERT INTO Locales (ID, LocationName, ParentID) VALUES (3, 'Los Angeles', 2);\nINSERT INTO Locales (ID, LocationName, ParentID) VALUES (4, 'UCLA', 3);\n\n-- Recursive query to get all locations under 'USA'\nWITH RECURSIVE LocationHierarchy AS (\n    SELECT ID, LocationName, ParentID\n    FROM Locales\n    WHERE ID = 1  -- Starting point (e.g., USA)\n    UNION ALL\n    SELECT l.ID, l.LocationName, l.ParentID\n    FROM Locales l\n    INNER JOIN LocationHierarchy lh ON lh.ID = l.ParentID\n)\nSELECT * FROM LocationHierarchy;