DECLARE \n    name VARCHAR2(30);\n    code VARCHAR2(10);\n    col_val VARCHAR2(30);\n    col2_val NUMBER;\nBEGIN\n    name := 'test';\n    code := '900';\n\n    BEGIN\n        -- Try to find an exact match\n        SELECT col1, col2 INTO col_val, col2_val \n        FROM t1 \n        WHERE col1 = name || ' ' || code;\n        \n        EXCEPTION\n            WHEN NO_DATA_FOUND THEN\n                BEGIN\n                    -- If no exact match, find any row with the name\n                    SELECT col1, col2 INTO col_val, col2_val \n                    FROM t1 \n                    WHERE col1 LIKE name || '%'\n                    ORDER BY col1\n                    FETCH FIRST 1 ROWS ONLY;\n                END;\n    END;\n\n    DBMS_OUTPUT.PUT_LINE('val:' || col_val || ' id:' || col2_val);\nEND;