ALTER PROCEDURE [EmployeeSearch]\n@PayrollNumber  varchar(50) = null,\n@isInternal     bit = null,\n@Firstname      varchar(50) = null,\n@Surname        varchar(50) = null\nAS\nSET NOCOUNT ON;\nSELECT EmployeeSearchView.*\nFROM EmployeeSearchView\nWHERE\nEmployeeSearchView.isInternal = @isInternal\nAND \n(\n    EmployeeSearchView.PayrollNumber LIKE '%' + NULLIF(@PayrollNumber, '') + '%'\n    OR EmployeeSearchView.Firstname LIKE '%' + NULLIF(@Firstname, '') + '%'\n    OR EmployeeSearchView.Surname LIKE '%' + NULLIF(@Surname, '') + '%'\n);