CREATE PROCEDURE SearchCards\n    @Keywords NVARCHAR(MAX)\nAS\nBEGIN\n    DECLARE @tblKeyword TABLE (Value NVARCHAR(100));\n    \n    -- Assuming the keywords are comma-separated\n    INSERT INTO @tblKeyword (Value)\n    SELECT value FROM STRING_SPLIT(@Keywords, ',');\n\n    SELECT Id, Name, Description\n    FROM dbo.Card\n    WHERE EXISTS (\n        SELECT 1\n        FROM @tblKeyword\n        WHERE CONTAINS(Card.Description, @tblKeyword.Value)\n    );\nEND;