-- Example of a table with common fields\nCREATE TABLE ExampleTable (\n    ID INT IDENTITY PRIMARY KEY,\n    CreatedOn DATETIME,\n    CreatedBy NVARCHAR(255),\n    SpecificField NVARCHAR(255)\n);\n\n-- Trigger to populate common fields\nCREATE TRIGGER trg_ExampleTable_Insert\nON ExampleTable\nAFTER INSERT\nAS\nBEGIN\n    UPDATE ExampleTable\n    SET CreatedOn = GETDATE(),\n        CreatedBy = SUSER_SNAME()\n    WHERE ID IN (SELECT ID FROM inserted);\nEND;