-- Parent table with common fields\nCREATE TABLE CommonFields (\n    CommonID INT IDENTITY PRIMARY KEY,\n    CreatedOn DATETIME NOT NULL DEFAULT GETDATE(),\n    CreatedBy NVARCHAR(255) NOT NULL\n);\n\n-- Child table referencing the parent table\nCREATE TABLE ChildTable (\n    ChildID INT IDENTITY PRIMARY KEY,\n    CommonID INT NOT NULL,\n    SpecificField NVARCHAR(255),\n    FOREIGN KEY (CommonID) REFERENCES CommonFields(CommonID)\n);