-- Create a table with a BLOB column\nCREATE TABLE example_table (\n    id INT PRIMARY KEY AUTO_INCREMENT,\n    data_blob BLOB\n);\n\n-- Insert text data into the BLOB column\nINSERT INTO example_table (data_blob) VALUES (CONVERT('This is a text data' USING utf8mb4));\n\n-- Retrieve the text data from the BLOB column\nSELECT CONVERT(data_blob USING utf8mb4) AS text_data FROM example_table;