CREATE TABLE problems (\n    id INT AUTO_INCREMENT PRIMARY KEY,\n    name VARCHAR(255),\n    description TEXT,\n    created_at DATETIME\n);\n\nCREATE TABLE relationships (\n    id INT AUTO_INCREMENT PRIMARY KEY,\n    type ENUM('Parent-child', 'Overlap', 'Addresses', 'Version'),\n    problem1_id INT,\n    problem2_id INT,\n    FOREIGN KEY (problem1_id) REFERENCES problems(id),\n    FOREIGN KEY (problem2_id) REFERENCES problems(id)\n);