-- Create a local table with the same structure as the remote table\nCREATE TABLE local_sample_table (\n    column_a CLOB\n);\n\n-- PL/SQL block to fetch and insert LOB data from remote table\nBEGIN\n    FOR rec IN (SELECT column_a FROM data_user.sample_table@TEST_LINK) LOOP\n        INSERT INTO local_sample_table (column_a)\n        VALUES (rec.column_a);\n    END LOOP;\n    COMMIT;\nEND;\n/