BEGIN\n    DBMS_SCHEDULER.create_job (\n        job_name        => 'TRANSFER_LOB_JOB',\n        job_type        => 'PLSQL_BLOCK',\n        job_action      => '\n        BEGIN\n            FOR rec IN (SELECT column_a FROM data_user.sample_table@TEST_LINK) LOOP\n                INSERT INTO local_sample_table (column_a)\n                VALUES (rec.column_a);\n            END LOOP;\n            COMMIT;\n        END;',\n        start_date      => SYSTIMESTAMP,\n        repeat_interval => 'FREQ=DAILY; INTERVAL=1', -- Adjust as needed\n        enabled         => TRUE\n    );\nEND;\n/