SELECT \n    username, \n    TheAverage, \n    TheCount \nFROM \n    (SELECT \n         username, \n         (SUM(rating) / COUNT(*)) AS TheAverage, \n         COUNT(*) AS TheCount \n     FROM \n         ratings \n     WHERE \n         month = 'Aug' \n     GROUP BY \n         username \n     HAVING \n         COUNT(*) > 1) AS subquery \nORDER BY \n    TheAverage DESC, \n    TheCount DESC;