DECLARE @sql NVARCHAR(MAX) = N'';\n\n-- Define the date filter\nDECLARE @dateFilter DATETIME = '2023-10-01';\n\n-- Generate the DROP and CREATE scripts\nSELECT @sql = @sql + '\nIF OBJECT_ID(''' + QUOTENAME(SCHEMA_NAME(schema_id)) + '.' + QUOTENAME(name) + ''', ''P'') IS NOT NULL\n    DROP PROCEDURE ' + QUOTENAME(SCHEMA_NAME(schema_id)) + '.' + QUOTENAME(name) + ';\nGO\n\n' + OBJECT_DEFINITION(object_id) + '\nGO\n\n'\nFROM sys.procedures\nWHERE modify_date >= @dateFilter\nORDER BY modify_date DESC;\n\n-- Print the generated script\nPRINT @sql;