WITH RECURSIVE EmployeeCTE AS (\n    SELECT \n        EmployeeID, \n        ManagerID, \n        EmployeeName, \n        0 AS Level\n    FROM Employees\n    WHERE ManagerID IS NULL\n    UNION ALL\n    SELECT \n        e.EmployeeID, \n        e.ManagerID, \n        e.EmployeeName, \n        c.Level + 1\n    FROM Employees e\n    INNER JOIN EmployeeCTE c ON e.ManagerID = c.EmployeeID\n)\nSELECT *\nFROM EmployeeCTE;