-- Surrogate Key Query Example\nSELECT * \nFROM PartPlantEngineerSurrogate PPE\nJOIN PartSurrogate P ON PPE.PartID = P.PartID\nJOIN Plant PL ON PPE.PlantID = PL.PlantID\nJOIN Engineer E ON PPE.EngineerID = E.EngineerID\nWHERE P.PartNumber = 'XYZ';\n\n-- Natural Key Query Example\nSELECT * \nFROM PartPlantEngineerNatural PPEN\nJOIN PartNatural PN ON PPEN.PartNumber = PN.PartNumber\nJOIN Plant PL ON PPEN.PlantID = PL.PlantID\nJOIN Engineer E ON PPEN.EngineerID = E.EngineerID\nWHERE PN.PartNumber = 'XYZ';