DECLARE @n INT = 10;\n\nWITH CTE AS (\n    SELECT \n        ROW_NUMBER() OVER (ORDER BY [YourSortColumn]) AS RowNum,\n        [YourPrimaryKey]\n    FROM \n        [YourTable]\n)\nDELETE FROM [YourTable]\nWHERE [YourPrimaryKey] IN (\n    SELECT [YourPrimaryKey]\n    FROM CTE\n    WHERE RowNum > @n\n);