SELECT \n    a.table_name,\n    a.constraint_name,\n    a.column_name,\n    c.owner AS referenced_owner,\n    c_pk.table_name AS referenced_table,\n    c_pk.constraint_name AS referenced_constraint,\n    c_pk.column_name AS referenced_column\nFROM \n    all_cons_columns a\nJOIN \n    all_constraints c ON a.owner = c.owner\n    AND a.constraint_name = c.constraint_name\nJOIN \n    all_cons_columns c_pk ON c.r_owner = c_pk.owner\n    AND c.r_constraint_name = c_pk.constraint_name\n    AND a.position = c_pk.position\nWHERE \n    c.constraint_type = 'R'\n    AND c_pk.table_name = 'FOO'\nORDER BY \n    a.table_name, a.constraint_name;