SELECT * FROM Employees\nWHERE \n  (MONTH(Birthday) = MONTH(@Today) AND DAY(Birthday) >= DAY(@Today))\n  OR\n  (MONTH(Birthday) = MONTH(@Today + @NumDays) AND DAY(Birthday) <= DAY(@Today + @NumDays))\n  OR\n  (MONTH(Birthday) > MONTH(@Today) AND MONTH(Birthday) < MONTH(@Today + @NumDays))\n  OR\n  (MONTH(@Today) > MONTH(@Today + @NumDays) AND (MONTH(Birthday) > MONTH(@Today) OR MONTH(Birthday) < MONTH(@Today + @NumDays)))