-- Creating a sample table\nCREATE TABLE TABLE_A (\n  ID NUMBER,\n  NAME VARCHAR2(50)\n);\n\n-- Inserting a row with an empty string\nINSERT INTO TABLE_A (ID, NAME) VALUES (100, '');\n\n-- Querying the table\nSELECT ID, NAME FROM TABLE_A WHERE ID = 100;\n\n-- The output will show:\n--  ID   NAME\n--  100  NULL\n\n-- Checking the length of the NAME column\nSELECT LENGTH(NAME) FROM TABLE_A WHERE ID = 100;\n\n-- The output will be:\n--  LENGTH(NAME)\n--  NULL