-- Trigger for INSERT\n   CREATE TRIGGER trg_YourTable_Insert\n   ON dbo.YourTable\n   AFTER INSERT\n   AS\n   BEGIN\n       INSERT INTO dbo.YourTable_Audit (ChangeType, ChangeDate, ChangedBy, Column1, Column2, ..., ColumnN)\n       SELECT 'I', GETDATE(), ORIGINAL_LOGIN(), i.Column1, i.Column2, ..., i.ColumnN\n       FROM inserted i;\n   END;\n   GO\n\n   -- Trigger for UPDATE\n   CREATE TRIGGER trg_YourTable_Update\n   ON dbo.YourTable\n   AFTER UPDATE\n   AS\n   BEGIN\n       INSERT INTO dbo.YourTable_Audit (ChangeType, ChangeDate, ChangedBy, Column1, Column2, ..., ColumnN)\n       SELECT 'U', GETDATE(), ORIGINAL_LOGIN(), d.Column1, d.Column2, ..., d.ColumnN\n       FROM deleted d;\n   END;\n   GO\n\n   -- Trigger for DELETE\n   CREATE TRIGGER trg_YourTable_Delete\n   ON dbo.YourTable\n   AFTER DELETE\n   AS\n   BEGIN\n       INSERT INTO dbo.YourTable_Audit (ChangeType, ChangeDate, ChangedBy, Column1, Column2, ..., ColumnN)\n       SELECT 'D', GETDATE(), ORIGINAL_LOGIN(), d.Column1, d.Column2, ..., d.ColumnN\n       FROM deleted d;\n   END;\n   GO