CREATE TABLE Users (\n    user_id INT PRIMARY KEY AUTO_INCREMENT,\n    username VARCHAR(255) NOT NULL\n);\n\nCREATE TABLE Companies (\n    company_id INT PRIMARY KEY AUTO_INCREMENT,\n    company_name VARCHAR(255) NOT NULL\n);\n\nCREATE TABLE Seats (\n    seat_id INT PRIMARY KEY AUTO_INCREMENT,\n    company_id INT,\n    FOREIGN KEY (company_id) REFERENCES Companies(company_id)\n);\n\nCREATE TABLE Products (\n    product_id INT PRIMARY KEY AUTO_INCREMENT,\n    product_name VARCHAR(255) NOT NULL\n);\n\nCREATE TABLE Modules (\n    module_id INT PRIMARY KEY AUTO_INCREMENT,\n    module_name VARCHAR(255) NOT NULL\n);\n\nCREATE TABLE Lessons (\n    lesson_id INT PRIMARY KEY AUTO_INCREMENT,\n    lesson_name VARCHAR(255) NOT NULL\n);\n\nCREATE TABLE UserSeats (\n    user_seat_id INT PRIMARY KEY AUTO_INCREMENT,\n    user_id INT,\n    seat_id INT,\n    FOREIGN KEY (user_id) REFERENCES Users(user_id),\n    FOREIGN KEY (seat_id) REFERENCES Seats(seat_id)\n);\n\nCREATE TABLE SeatProducts (\n    seat_product_id INT PRIMARY KEY AUTO_INCREMENT,\n    seat_id INT,\n    product_id INT,\n    FOREIGN KEY (seat_id) REFERENCES Seats(seat_id),\n    FOREIGN KEY (product_id) REFERENCES Products(product_id)\n);\n\nCREATE TABLE ProductModules (\n    product_module_id INT PRIMARY KEY AUTO_INCREMENT,\n    product_id INT,\n    module_id INT,\n    FOREIGN KEY (product_id) REFERENCES Products(product_id),\n    FOREIGN KEY (module_id) REFERENCES Modules(module_id)\n);\n\nCREATE TABLE ModuleLessons (\n    module_lesson_id INT PRIMARY KEY AUTO_INCREMENT,\n    module_id INT,\n    lesson_id INT,\n    FOREIGN KEY (module_id) REFERENCES Modules(module_id),\n    FOREIGN KEY (lesson_id) REFERENCES Lessons(lesson_id)\n);\n\nCREATE TABLE UserLessons (\n    user_lesson_id INT PRIMARY KEY AUTO_INCREMENT,\n    user_id INT,\n    lesson_id INT,\n    score INT,\n    status VARCHAR(50),\n    FOREIGN KEY (user_id) REFERENCES Users(user_id),\n    FOREIGN KEY (lesson_id) REFERENCES Lessons(lesson_id)\n);\n\nCREATE TABLE Certifications (\n    certification_id INT PRIMARY KEY AUTO_INCREMENT,\n    user_id INT,\n    product_id INT,\n    certification_date TIMESTAMP,\n    FOREIGN KEY (user_id) REFERENCES Users(user_id),\n    FOREIGN KEY (product_id) REFERENCES Products(product_id)\n);