INSERT INTO roles (role_name) VALUES ('admin'), ('teacher'), ('student');\n   INSERT INTO permissions (permission_name) VALUES ('view_students'), ('view_teachers');\n\n   INSERT INTO role_permissions (role_id, permission_id) VALUES \n   ((SELECT id FROM roles WHERE role_name='admin'), (SELECT id FROM permissions WHERE permission_name='view_students')),\n   ((SELECT id FROM roles WHERE role_name='admin'), (SELECT id FROM permissions WHERE permission_name='view_teachers')),\n   ((SELECT id FROM roles WHERE role_name='teacher'), (SELECT id FROM permissions WHERE permission_name='view_students'));