-- Step 1: Drop the index on the computed column\nDROP INDEX idxUpperBaseColumn ON testTable;\n\n-- Step 2: Alter the base column to increase its length\nALTER TABLE testTable\nALTER COLUMN baseColumn VARCHAR(20); -- Change length as needed\n\n-- Step 3: Drop and recreate the computed column\nALTER TABLE testTable\nDROP COLUMN upperBaseColumn;\n\nALTER TABLE testTable\nADD upperBaseColumn AS (UPPER(baseColumn));\n\n-- Step 4: Recreate the index on the computed column\nCREATE INDEX idxUpperBaseColumn ON testTable (upperBaseColumn);