SELECT \n    pe.prodtree_element_name_l, \n    rs.resource_value\nFROM \n    prodtree_element pe\nCROSS APPLY (\n    SELECT TOP 1 rs.resource_value\n    FROM resource_shortstrings rs\n    WHERE \n        rs.resource_key = pe.prodtree_element_name_l_rk \n        AND rs.language_id = '5'\n        AND pe.prodtree_element_name_l <> ''\n) AS rs\nWHERE \n    pe.prodtree_element_name_l <> ''\nGROUP BY \n    pe.prodtree_element_name_l, \n    rs.resource_value