-- Assuming your table structure is something like this:\n-- MyTable (uniqueId UNIQUEIDENTIFIER, column1, column2, PreviousId UNIQUEIDENTIFIER, ...)\n\nDECLARE @Id UNIQUEIDENTIFIER;\nSET @Id = 'your-original-id-here';\n\n-- Insert a new row with a new uniqueidentifier and copy selected columns, leaving others as NULL\nINSERT INTO MyTable (uniqueId, column1, column2, PreviousId, ...)\nSELECT NEWID(), column1, column2, uniqueId, ...\nFROM MyTable\nWHERE uniqueId = @Id;