CREATE FUNCTION dbo.ValidateInput(@input NVARCHAR(100))\nRETURNS NVARCHAR(200)\nAS\nBEGIN\n    DECLARE @result NVARCHAR(200);\n\n    -- Check for invalid input (e.g., NULL or empty string)\n    IF @input IS NULL OR LEN(@input) = 0\n    BEGIN\n        SET @result = 'Error: Invalid input. Input cannot be NULL or empty.';\n    END\n    ELSE\n    BEGIN\n        -- Process valid input\n        SET @result = 'Valid input: ' + @input;\n    END\n\n    RETURN @result;\nEND\nGO