WITH OrderedEvents AS (\n    SELECT\n        event_id,\n        event_value,\n        event_timestamp,\n        ROW_NUMBER() OVER (PARTITION BY event_id ORDER BY event_timestamp) AS rn,\n        LAG(event_value) OVER (PARTITION BY event_id ORDER BY event_timestamp) AS prev_value\n    FROM\n        your_table\n)\nDELETE FROM your_table\nWHERE\n    (event_id, event_timestamp) IN (\n        SELECT\n            event_id, event_timestamp\n        FROM\n            OrderedEvents\n        WHERE\n            event_value = prev_value\n    );