CREATE VIEW RLIC2GIS AS \nSELECT REPLACE(CAST(RCAPIN AS VARCHAR(255)), ' ', '') AS RCAPIN13,\n       RLICNO, RONAME, ROADR1, ROADR2, ROCITY, ROSTAT, ROZIP1, \n       ROZIP2, RGRID, RRADR1, RRADR2, RANAME, RAADR1, RAADR2, \n       RACITY, RASTAT, RAZIP1, RAZIP2, REGRES, RPENDI, RBLDGT, \n       ROWNOC, RRCODE, RROOMS, RUNITS, RTUNIT, RPAID, RAMTPD, \n       RMDYPD, RRFUSE, RNUMCP, RDATCP, RINSP, RCAUKY, RCAPIN, \n       RAMTYR, RYREXP, RDELET, RVARIA, RMDYIN, RDTLKI, ROPHN1, \n       ROPHN2, ROCOM1, ROCOM2, RAPHN1, RAPHN2, RACOM1, RACOM2, \n       RNOTES \nFROM RLIC2;