DECLARE @MyXml XML\nSET @MyXml = '<root></root>'\n\n;WITH XMLNAMESPACES ('http://www.w3.org/2001/XMLSchema-instance' AS xsi)\n-- Assuming you have a table named MyTable with a column named MyColumn\nINSERT INTO @MyXml.modify('\n   insert {for $x in sql:column("MyColumn")\n            return <myNode>{$x}</myNode>}\n   into (/root[1])\n')\nSELECT MyColumn\nFROM MyTable\nFOR XML PATH(''), TYPE\n\nSELECT @MyXml