WITH RankedRows AS (\n    SELECT \n        rowID, \n        longitude, \n        latitude, \n        businessName, \n        url, \n        caption,\n        ROW_NUMBER() OVER (\n            PARTITION BY longitude, latitude, businessName \n            ORDER BY \n                CASE \n                    WHEN url IS NOT NULL THEN 1 \n                    WHEN caption IS NOT NULL THEN 2 \n                    ELSE 3 \n                END\n        ) AS rn\n    FROM \n        your_table_name\n)\nDELETE FROM your_table_name\nWHERE rowID IN (\n    SELECT rowID\n    FROM RankedRows\n    WHERE rn > 1\n);