DECLARE @Result INT;\n\n-- Acquire application lock\nEXEC @Result = sp_getapplock @Resource = 'YourRowIdentifier', @LockMode = 'Exclusive', @LockOwner = 'Session';\n\nIF @Result >= 0\nBEGIN\n    -- Perform operations that require the lock\n    -- ...\n\n    -- Release the lock\n    EXEC sp_releaseapplock @Resource = 'YourRowIdentifier', @LockOwner = 'Session';\nEND\nELSE\nBEGIN\n    PRINT 'Failed to acquire lock';\nEND