-- Start a server-side trace\nDECLARE @TraceID INT\nDECLARE @MaxFileSize BIGINT\nDECLARE @FileName NVARCHAR(245)\nSET @MaxFileSize = 5 -- Specify the file size limit in MB\nSET @FileName = N'C:\TraceFiles\SQLTrace' -- Specify the path for the trace files\n\n-- Create the trace\nEXEC sp_trace_create @TraceID OUTPUT, 0, @FileName, @MaxFileSize, NULL\n\n-- Set the events and columns to capture\n-- Example: Capture all SQL:BatchCompleted and SQL:StmtCompleted events\nEXEC sp_trace_setevent @TraceID, 10, 1, 1\nEXEC sp_trace_setevent @TraceID, 10, 12, 1\nEXEC sp_trace_setevent @TraceID, 10, 13, 1\nEXEC sp_trace_setevent @TraceID, 10, 14, 1\nEXEC sp_trace_setevent @TraceID, 10, 15, 1\nEXEC sp_trace_setevent @TraceID, 10, 16, 1\nEXEC sp_trace_setevent @TraceID, 10, 17, 1\nEXEC sp_trace_setevent @TraceID, 10, 18, 1\nEXEC sp_trace_setevent @TraceID, 10, 25, 1\nEXEC sp_trace_setevent @TraceID, 10, 26, 1\nEXEC sp_trace_setevent @TraceID, 10, 35, 1\n\n-- Start the trace\nEXEC sp_trace_setstatus @TraceID, 1