WITH CTE AS (\n    SELECT \n        *,\n        ROW_NUMBER() OVER (\n            PARTITION BY longitude, latitude, BusinessName, Phone \n            ORDER BY \n                CASE WHEN webAddress IS NOT NULL THEN 1 ELSE 2 END,\n                CASE WHEN caption1 IS NOT NULL THEN 1 ELSE 2 END,\n                CASE WHEN caption2 IS NOT NULL THEN 1 ELSE 2 END,\n                RecordID\n        ) AS row_num\n    FROM MyTable\n)\nDELETE FROM CTE\nWHERE row_num > 1;