SELECT \n    CustomerID,\n    Service,\n    SUM(CASE WHEN TransType = 'CASH' THEN SubTotal ELSE 0 END) AS Cash,\n    SUM(CASE WHEN TransType = 'CHECK' THEN SubTotal ELSE 0 END) AS Check,\n    SUM(CASE WHEN TransType = 'CREDIT' THEN SubTotal ELSE 0 END) AS Credit,\n    SUM(CASE WHEN TransType = 'REMOTE' THEN SubTotal ELSE 0 END) AS Remote,\n    SUM(SubTotal) AS SubTotal,\n    SUM(Tax) AS Tax,\n    SUM(NetTotal) AS NetTotal\nFROM\n    (SELECT \n         CustomerID,\n         Service,\n         TransType,\n         SubTotal,\n         Tax,\n         (SubTotal - ISNULL(Tax, 0)) AS NetTotal\n     FROM\n         YourTableName) AS SourceData\nGROUP BY \n    CustomerID,\n    Service\nORDER BY \n    CustomerID,\n    Service;