-- Create a new trace\nDECLARE @TraceID INT\nEXEC sp_trace_create @TraceID OUTPUT, 0, N'C:\Temp\MyTraceFile.trc', NULL, NULL;\n\n-- Add events to the trace\nEXEC sp_trace_setevent @TraceID, 10, 1, 1; -- RPC:Completed\nEXEC sp_trace_setevent @TraceID, 12, 1, 1; -- SQL:BatchCompleted\n\n-- Add filters (optional)\n-- Filter by database ID\nDECLARE @DBID INT\nSET @DBID = DB_ID('YourDatabaseName')\nEXEC sp_trace_setfilter @TraceID, 3, 0, 0, @DBID;\n\n-- Start the trace\nEXEC sp_trace_setstatus @TraceID, 1;\n\n-- To stop and delete the trace\n-- EXEC sp_trace_setstatus @TraceID, 0; -- Stop the trace\n-- EXEC sp_trace_setstatus @TraceID, 2; -- Delete the trace