-- Table to store unique key fields\nCREATE TABLE KeyTable (\n    KeyID INT PRIMARY KEY IDENTITY,\n    KeyField NVARCHAR(255) NOT NULL\n);\n\n-- Table to store the relationship between keys and keywords\nCREATE TABLE KeywordTable (\n    KeyID INT,\n    Keyword NVARCHAR(255),\n    PRIMARY KEY (KeyID, Keyword),\n    FOREIGN KEY (KeyID) REFERENCES KeyTable(KeyID)\n);\n\n-- Index to speed up keyword searches\nCREATE INDEX idx_keyword ON KeywordTable(Keyword);