-- Table to store languages\nCREATE TABLE Languages (\n    LanguageID INT PRIMARY KEY,\n    LanguageCode VARCHAR(10) UNIQUE NOT NULL,\n    LanguageName VARCHAR(100) NOT NULL\n);\n\n-- Main entity table (e.g., Products)\nCREATE TABLE Products (\n    ProductID INT PRIMARY KEY,\n    SKU VARCHAR(50) UNIQUE NOT NULL,\n    Price DECIMAL(10, 2) NOT NULL,\n    CreatedAt TIMESTAMP DEFAULT CURRENT_TIMESTAMP\n);\n\n-- Translation table for localized product data\nCREATE TABLE ProductTranslations (\n    TranslationID INT PRIMARY KEY AUTO_INCREMENT,\n    ProductID INT NOT NULL,\n    LanguageID INT NOT NULL,\n    ProductName VARCHAR(255) NOT NULL,\n    ProductDescription TEXT,\n    FOREIGN KEY (ProductID) REFERENCES Products(ProductID),\n    FOREIGN KEY (LanguageID) REFERENCES Languages(LanguageID),\n    UNIQUE (ProductID, LanguageID)\n);\n\n-- Example data insertion\nINSERT INTO Languages (LanguageID, LanguageCode, LanguageName) VALUES (1, 'en', 'English');\nINSERT INTO Languages (LanguageID, LanguageCode, LanguageName) VALUES (2, 'es', 'Spanish');\n\nINSERT INTO Products (ProductID, SKU, Price) VALUES (1, 'SKU12345', 19.99);\n\nINSERT INTO ProductTranslations (ProductID, LanguageID, ProductName, ProductDescription) \nVALUES (1, 1, 'Product Name in English', 'Product Description in English');\nINSERT INTO ProductTranslations (ProductID, LanguageID, ProductName, ProductDescription) \nVALUES (1, 2, 'Nombre del Producto en Español', 'Descripción del Producto en Español');