-- Table to store types of measurements (e.g., temperature, dissolved oxygen)\nCREATE TABLE measurement_type (\n    id SERIAL PRIMARY KEY,\n    type_name TEXT UNIQUE NOT NULL\n);\n\n-- Table to store units of measurements with a reference to their types\nCREATE TABLE measurement_unit (\n    id SERIAL PRIMARY KEY,\n    unit_symbol TEXT UNIQUE NOT NULL, -- Use standardized unit symbols\n    unit_name TEXT NOT NULL,\n    measurement_type_id INT NOT NULL,\n    CONSTRAINT fk_measurement_type\n        FOREIGN KEY(measurement_type_id) \n        REFERENCES measurement_type(id)\n);\n\n-- Table to store the actual measurement values along with references to their types and units\nCREATE TABLE measured_value (\n    id SERIAL PRIMARY KEY,\n    measurement_value NUMERIC NOT NULL,\n    measurement_type_id INT NOT NULL,\n    measurement_unit_id INT NOT NULL,\n    timestamp TIMESTAMPTZ NOT NULL DEFAULT NOW(),\n    CONSTRAINT fk_measurement_type\n        FOREIGN KEY(measurement_type_id)\n        REFERENCES measurement_type(id),\n    CONSTRAINT fk_measurement_unit\n        FOREIGN KEY(measurement_unit_id)\n        REFERENCES measurement_unit(id)\n);\n\n-- Insert some sample data for measurement types\nINSERT INTO measurement_type (type_name) VALUES ('temperature');\nINSERT INTO measurement_type (type_name) VALUES ('dissolved oxygen');\n\n-- Insert some sample data for measurement units (standardized symbols)\nINSERT INTO measurement_unit (unit_symbol, unit_name, measurement_type_id) \nVALUES ('C', 'Celsius', 1),\n       ('F', 'Fahrenheit', 1),\n       ('K', 'Kelvin', 1),\n       ('%', 'percent', 2),\n       ('mg/L', 'milligrams per liter', 2);\n\n-- Insert some sample measured values\nINSERT INTO measured_value (measurement_value, measurement_type_id, measurement_unit_id) \nVALUES (25.0, 1, 1),\n       (77.0, 1, 2),\n       (298.15, 1, 3),\n       (8.5, 2, 4),\n       (9.0, 2, 5);