CREATE PROCEDURE UpdateData\n       @TableIdentifier INT,\n       @Column1Value TYPE,\n       @Column2Value TYPE\n   AS\n   BEGIN\n       IF @TableIdentifier = 1\n       BEGIN\n           UPDATE MappingQuery1\n           SET column1 = @Column1Value, column2 = @Column2Value\n           WHERE condition;\n       END\n       ELSE IF @TableIdentifier = 2\n       BEGIN\n           UPDATE MappingQuery2\n           SET column1 = @Column1Value, column2 = @Column2Value\n           WHERE condition;\n       END\n   END;