-- Step 1: Insert the new post\nINSERT INTO wp_posts (\n    post_author,\n    post_date,\n    post_date_gmt,\n    post_content,\n    post_title,\n    post_status,\n    comment_status,\n    ping_status,\n    post_name,\n    post_modified,\n    post_modified_gmt,\n    post_type\n) VALUES (\n    1, -- post_author\n    NOW(), -- post_date\n    NOW(), -- post_date_gmt\n    'Your post content here', -- post_content\n    'Your post title here', -- post_title\n    'publish', -- post_status\n    'open', -- comment_status\n    'open', -- ping_status\n    'your-post-title-here', -- post_name (slug)\n    NOW(), -- post_modified\n    NOW(), -- post_modified_gmt\n    'post' -- post_type\n);\n\n-- Get the ID of the newly inserted post\nSET @post_id = LAST_INSERT_ID();\n\n-- Step 2: Find the term_taxonomy_id for the desired category\n-- Replace 'your-category-slug' with the actual slug of your category\nSELECT @term_taxonomy_id := term_taxonomy_id\nFROM wp_term_taxonomy\nJOIN wp_terms ON wp_terms.term_id = wp_term_taxonomy.term_id\nWHERE wp_terms.slug = 'your-category-slug'\n  AND wp_term_taxonomy.taxonomy = 'category';\n\n-- Step 3: Associate the new post with the category\nINSERT INTO wp_term_relationships (\n    object_id,\n    term_taxonomy_id\n) VALUES (\n    @post_id, -- object_id (post ID)\n    @term_taxonomy_id -- term_taxonomy_id (category)\n);