CREATE OR REPLACE PROCEDURE manage_table_size IS\n    v_row_count NUMBER;\n    v_limit CONSTANT NUMBER := 1000000;\nBEGIN\n    -- Get the current row count\n    SELECT COUNT(*)\n    INTO v_row_count\n    FROM event_log;\n\n    -- If the row count exceeds the limit, delete the oldest rows\n    IF v_row_count > v_limit THEN\n        DELETE FROM event_log\n        WHERE rowid IN (\n            SELECT rowid\n            FROM event_log\n            ORDER BY log_date ASC\n            FETCH FIRST (v_row_count - v_limit) ROWS ONLY\n        );\n        COMMIT;\n    END IF;\nEND;\n/