-- List of param values to match\nWITH param_list AS (\n    SELECT 1 AS value\n    UNION ALL\n    SELECT 2\n    UNION ALL\n    SELECT 3\n)\n\n-- Find all sources that have all values in the param_list\nSELECT s.id, s.name\nFROM source s\nJOIN params p ON s.id = p.source\nWHERE p.value IN (SELECT value FROM param_list)\nGROUP BY s.id, s.name\nHAVING COUNT(DISTINCT p.value) = (SELECT COUNT(*) FROM param_list);