CREATE TABLE Questions \n(\n    USER_ID integer FOREIGN KEY REFERENCES User_info(USER_ID) \n                    PRIMARY KEY \n                    CHECK (USER_ID > 0), \n    QUESTION_ID integer FOREIGN KEY REFERENCES Tags(QUESTION_ID) \n                        NOT NULL \n                        CHECK (USER_ID > 0), \n    QUESTION_BODY nvarchar(4000) NOT NULL,\n    TITLE nvarchar(60) NOT NULL,  // Ensures TITLE cannot be NULL\n    MODERATOR_REMOVAL boolean NOT NULL,\n    SENT_TIME varchar(15) NOT NULL\n);