-- Table for user information\nCREATE TABLE tblusers (\n    UsrID INT AUTO_INCREMENT PRIMARY KEY,\n    FirstName VARCHAR(100),\n    LastName VARCHAR(100),\n    DOB DATE,\n    PhoneNumber VARCHAR(15),\n    -- Other fields\n);\n\n-- Table for storing user pictures\nCREATE TABLE tblpictures (\n    PictureID INT AUTO_INCREMENT PRIMARY KEY,\n    UsrID INT,\n    PictureURL VARCHAR(255),\n    IsDefault BOOLEAN DEFAULT FALSE,\n    FOREIGN KEY (UsrID) REFERENCES tblusers(UsrID)\n);\n\n-- Table for storing thumbnails\nCREATE TABLE tblthumbnails (\n    ThumbnailID INT AUTO_INCREMENT PRIMARY KEY,\n    PictureID INT,\n    ThumbnailURL VARCHAR(255),\n    FOREIGN KEY (PictureID) REFERENCES tblpictures(PictureID)\n);